import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const MedTracker = () => {
  const days = ["Mon", "Tues", "Wed", "Thur", "Fri", "Sat", "Sun"];
  const [selectedDay, setSelectedDay] = useState(null);
  const navigate = useNavigate();

  const goBack = () => {
    navigate("/dashboard");
  };

  

  const handleDayClick = (day) => {
    setSelectedDay(day === selectedDay ? null : day);
  };


  return (
    <div className="min-h-screen p-6 flex flex-col items-start">
      <div className="mb-10 mt-12 ml-28 flex items-center gap-4">
        <button onClick={goBack}>
          <div>
            <svg
              width="70"
              height="62"
              viewBox="0 0 48 48"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <rect width="48" height="48" rx="16" fill="white" />
              <rect
                x="0.5"
                y="0.5"
                width="47"
                height="47"
                rx="15.5"
                stroke="black"
                stroke-opacity="0.35"
              />
              <path
                fill-rule="evenodd"
                clip-rule="evenodd"
                d="M26.9315 27.9596C27.1647 28.1969 27.1655 28.5824 26.9333 28.8207C26.7221 29.0374 26.391 29.0577 26.1573 28.8814L26.0904 28.8226L21.2824 23.9319C21.0697 23.7155 21.0504 23.3761 21.2244 23.1373L21.2824 23.069L26.0903 18.1775C26.3236 17.9402 26.701 17.941 26.9332 18.1792C27.1444 18.3959 27.1629 18.7342 26.9893 18.9722L26.9315 19.0403L22.5479 23.5006L26.9315 27.9596Z"
                fill="#040415"
              />
            </svg>
          </div>
        </button>
        <h2 className="text-black text-4xl font-bold">Medication Tracker</h2>
      </div>
      <div className="ml-32 pb-4">
        <h2 className="text-black text-xl font-bold mt-2">Weekly Log</h2>
      </div>
      <div className="ml-32 mt-4 flex gap-24">
        {days.map((day) => (
          <button
            key={day}
            className="flex flex-col items-center text-lg font-semibold mb-24"
            onClick={() => handleDayClick(day)}
            style={{
              color: "black",
            }}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="50"
              height="45"
              viewBox="0 0 26 25"
              fill="none"
              className="mb-1"
            >
              <path
                d="M21.5938 23.4375H4.40625C3.1123 23.4375 2.0625 22.3877 2.0625 21.0938V3.90625C2.0625 2.6123 3.1123 1.5625 4.40625 1.5625H21.5938C22.8877 1.5625 23.9375 2.6123 23.9375 3.90625V21.0938C23.9375 22.3877 22.8877 23.4375 21.5938 23.4375ZM13.6885 8.68652L7.0625 15.3125L6.75488 18.1006C6.71582 18.4717 7.02832 18.7891 7.4043 18.75L10.1924 18.4424L16.8184 11.8164C16.9307 11.7041 16.9307 11.5186 16.8184 11.4014L14.1084 8.69141C13.9863 8.57422 13.8008 8.57422 13.6885 8.68652ZM18.9082 8.06152L17.4385 6.5918C16.9795 6.13281 16.2373 6.13281 15.7832 6.5918L14.6553 7.71973C14.543 7.83203 14.543 8.01758 14.6553 8.13477L17.3652 10.8447C17.4775 10.957 17.6631 10.957 17.7803 10.8447L18.9082 9.7168C19.3623 9.2627 19.3623 8.52051 18.9082 8.06152Z"
                fill={selectedDay === day ? "#407CE2" : "#D9D9D9"}
              />
            </svg>
            {day}
          </button>
        ))}
      </div>

      
    </div>
  );
};

export default MedTracker;
