import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const SymptomTracker = () => {
  const days = ["Mon", "Tues", "Wed", "Thur", "Fri", "Sat", "Sun"];
  const allSymptoms = [
    "Fatigue",
    "Joint pain",
    "Stiffness",
    "Swelling",
    "Tenderness",
    "Limited range of motion",
    "Muscle weakness",
  ];
  const moods = ["Sad", "Neutral", "Happy"];

  const [text, setText] = useState("");
  const [painLevel, setPainLevel] = useState("1");
  const [selectedDay, setSelectedDay] = useState(null);
  const [selectedSymptoms, setSelectedSymptoms] = useState([]);
  const [selectedMood, setSelectedMood] = useState(null);
  const navigate = useNavigate();

  const handleDayClick = (day) => {
    setSelectedDay(day === selectedDay ? null : day);
  };

  const handleSymptomToggle = (symptom) => {
    if (selectedSymptoms.includes(symptom)) {
      setSelectedSymptoms(selectedSymptoms.filter((s) => s !== symptom));
    } else {
      setSelectedSymptoms([...selectedSymptoms, symptom]);
    }
  };

  const handleMoodClick = (mood) => {
    setSelectedMood(mood === selectedMood ? null : mood);
  };

  const goBack = () => {
    navigate("/dashboard");
  };

  return (
    <div className="min-h-screen p-6 flex flex-col">
      {/* Header */}
      <div className="flex items-center gap-4 mb-10 mt-12 ml-28">
        <button onClick={goBack}>
          {/* Back Arrow SVG */}
          <svg
            width="70"
            height="62"
            viewBox="0 0 48 48"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <rect width="48" height="48" rx="16" fill="white" />
            <rect
              x="0.5"
              y="0.5"
              width="47"
              height="47"
              rx="15.5"
              stroke="black"
              strokeOpacity="0.35"
            />
            <path
              fillRule="evenodd"
              clipRule="evenodd"
              d="M26.9315 27.9596C27.1647 28.1969 27.1655 28.5824 26.9333 28.8207C26.7221 29.0374 26.391 29.0577 26.1573 28.8814L26.0904 28.8226L21.2824 23.9319C21.0697 23.7155 21.0504 23.3761 21.2244 23.1373L21.2824 23.069L26.0903 18.1775C26.3236 17.9402 26.701 17.941 26.9332 18.1792C27.1444 18.3959 27.1629 18.7342 26.9893 18.9722L26.9315 19.0403L22.5479 23.5006L26.9315 27.9596Z"
              fill="#040415"
            />
          </svg>
        </button>
        <h2 className="text-black text-4xl font-bold">Symptom Tracker</h2>
      </div>

      {/* Weekly Log */}
      <div className="ml-32 mb-2">
        <h2 className="text-xl font-bold text-black">Weekly Log</h2>
      </div>
      <div className="ml-32 mt-2 flex gap-16 mb-10">
        {days.map((day) => (
          <button
            key={day}
            onClick={() => handleDayClick(day)}
            className="flex flex-col items-center text-lg font-semibold"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="40"
              height="45"
              viewBox="0 0 26 25"
              fill="none"
              className="mb-1"
            >
              <path
                d="M21.5938 23.4375H4.40625C3.1123 23.4375 2.0625 22.3877 2.0625 21.0938V3.90625C2.0625 2.6123 3.1123 1.5625 4.40625 1.5625H21.5938C22.8877 1.5625 23.9375 2.6123 23.9375 3.90625V21.0938C23.9375 22.3877 22.8877 23.4375 21.5938 23.4375Z"
                fill={selectedDay === day ? "#407CE2" : "#D9D9D9"}
              />
            </svg>
            {day}
          </button>
        ))}
      </div>

      {/* Daily Log Container */}
      <div className="relative w-[940px] mx-32 p-6 rounded-3xl border-2 border-[#DADADA] mb-12">
        <h2 className="text-xl font-bold mb-4">Daily Log</h2>

        {/* Edit Icon */}
        <div className="absolute top-6 right-6">
          <div className="flex items-center justify-center w-[83px] h-[72px] rounded-3xl border-2 border-[#DADADA]">
            {/* Edit SVG */}
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="35"
              height="35"
              viewBox="0 0 26 26"
              fill="none"
            >
              <path
                fillRule="evenodd"
                clipRule="evenodd"
                d="M19.9323 4.03013L21.2823 5.38013C22.0723 6.16013 22.0723 7.43013 21.2823 8.21013L8.05231 21.4401H3.87231V17.2601L14.2723 6.85013L17.1023 4.03013C17.8823 3.25013 19.1523 3.25013 19.9323 4.03013ZM5.87231 19.4401L7.28231 19.5001L17.1023 9.67013L15.6923 8.26013L5.87231 18.0801V19.4401Z"
                fill="#407CE2"
              />
            </svg>
          </div>
        </div>

        {/* Symptoms */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-black mb-2">Choose your symptoms</h3>
          <div className="flex flex-wrap gap-3">
            {allSymptoms.map((symptom) => (
              <label
                key={symptom}
                className={`cursor-pointer rounded-full px-4 py-1 border-2 text-sm font-medium ${
                  selectedSymptoms.includes(symptom)
                    ? "bg-blue-100 text-blue-700 border-blue-400"
                    : "bg-[#ECF1FF] text-gray-700 border-[#DADADA]"
                }`}
              >
                <input
                  type="checkbox"
                  className="sr-only"
                  checked={selectedSymptoms.includes(symptom)}
                  onChange={() => handleSymptomToggle(symptom)}
                />
                {symptom}
              </label>
            ))}
          </div>
        </div>

        {/* Notes */}
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type how you feel..."
          className="w-full h-[200px] rounded-md border border-[#DADADA] bg-[#ECF1FF] p-4 text-md focus:outline-none focus:ring-2 focus:ring-blue-300"
        />
      </div>

      {/* Pain Level */}
      <div className="ml-40 w-[880px] mb-6">
        <label className="text-xl font-bold mb-2 block">Pain</label>
        <select
          value={painLevel}
          onChange={(e) => setPainLevel(e.target.value)}
          className="w-full h-[47px] rounded-md border-2 border-[#DADADA] bg-[#ECF1FF] p-3 text-md focus:outline-none focus:ring-2 focus:ring-blue-300"
        >
          <option value="1">1 - Mild</option>
          <option value="2">2 - Low</option>
          <option value="3">3 - Moderate</option>
          <option value="4">4 - High</option>
          <option value="5">5 - Severe</option>
        </select>
      </div>

      {/* Mood Selection */}
      <div className="ml-40 w-[880px] mb-20">
        <label className="text-xl font-bold mb-2 block">Mood</label>
        <div className="flex gap-6 mt-2">
          {moods.map((mood) => (
            <label
              key={mood}
              className={`cursor-pointer rounded-full px-6 py-2 border-2 text-sm font-medium ${
                selectedMood === mood
                  ? "bg-blue-100 text-blue-700 border-blue-400"
                  : "bg-[#ECF1FF] text-gray-700 border-[#DADADA]"
              }`}
            >
              <input
                type="radio"
                name="mood"
                className="sr-only"
                checked={selectedMood === mood}
                onChange={() => handleMoodClick(mood)}
              />
              {mood}
            </label>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SymptomTracker;
