import React from "react";


const Settings = () => {
  const handleLogout = () => {
    console.log("Logged out");
    window.location.href = "/";
  };

  return (
    <div className="min-h-screen p-6 flex justify-center items-start">
      <div className="bg-white w-full max-w-2xl rounded-3xl shadow-lg p-10 space-y-10">
        <div>
          <h1 className="text-4xl font-bold mb-2">Settings</h1>
          <p className="text-gray-500">Manage your Profile and Account</p>
        </div>

        {/* Profile Section */}
        <section className="space-y-2">
          <h2 className="text-xl font-semibold"> Profile</h2>
          <p className="text-gray-600">
            Update your personal information
          </p>
        </section>

        {/* Account Section */}
        <section className="space-y-2">
          <h2 className="text-xl font-semibold"> Account</h2>
          <p className="text-gray-600">
            Change your password and manage sessions
          </p>
        </section>



        {/* Logout */}
        <div className="pt-6 border-t border-gray-300">
          <button
            onClick={handleLogout}
            className="flex items-center justify-center gap-2 w-full text-red-600 border border-red-500 hover:bg-red-500 hover:text-white font-semibold py-2.5 px-5 rounded-xl transition duration-200"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default Settings;
