import React from "react";

const Settings = () => {
  const handleLogout = () => {
    // Add your logout logic here (clear tokens, etc.)
    console.log("Logged out");
    window.location.href = "/Login"; // Redirect to login page
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex justify-center items-start">
      <div className="bg-white w-full max-w-xl rounded-2xl shadow-md p-8 space-y-6">
        <h1 className="text-3xl font-bold text-gray-800">Settings</h1>

        {/* Profile Section */}
        <div>
          <h2 className="text-xl font-semibold text-gray-700 mb-2">Profile</h2>
          <p className="text-gray-600">Update your personal information here.</p>
        </div>

        {/* Account Section */}
        <div>
          <h2 className="text-xl font-semibold text-gray-700 mb-2">Account</h2>
          <p className="text-gray-600">Manage account preferences and security settings.</p>
        </div>

        {/* Logout */}
        <div className="pt-6 border-t border-gray-200">
          <button
            onClick={handleLogout}
            className="w-full text-red-600 border border-red-500 hover:bg-red-500 hover:text-white font-semibold py-2 px-4 rounded-xl transition"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default Settings;
